import React, { Component } from "react";

import {
    Button, Modal, ModalHeader,
    ModalBody, ModalFooter,
    Form, FormGroup, Input, Label
} from "reactstrap";

export default class CustomModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activeItem: this.props.activeItem
        };
    }

    handleChange = e => {
        let { name, value } = e.target;
        if (e.target.type === "checkbox") {
            value = e.target.checked;
        }
        const activeItem = {
            ...this.state.activeItem, [name]: value
        };
        this.setState({ activeItem });
    };

    render() {
        const { toggle, onSave } = this.props;
        return (
            <Modal isOpen={true} toggle={toggle}>
                <ModalHeader toggle={toggle}>Todo Item</ModalHeader>
                <ModalBody>
                    <Form>
                        <FormGroup>
                            <Label for="title">Title</Label>
                            <Input
                                type="text"
                                name="title"
                                value={this.state.activeItem.title}
                                onChange={this.handleChange}
                                placeholder="Enter Todo Title"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">Description</Label>
                            <Input
                                type="text"
                                name="description"
                                value={this.state.activeItem.description}
                                onChange={this.handleChange}
                                placeholder="Enter Todo description"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">Type de t√¢che</Label>
                            <Input
                                type="text"
                                name="task"
                                value={this.state.activeItem.task}
                                onChange={this.handleChange}
                                placeholder="Enter todo type task"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">date start task</Label>
                            <Input
                                type="text"
                                name="description"
                                value={this.state.activeItem.date_start}
                                onChange={this.handleChange}
                                placeholder="Enter date start task"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">time start task</Label>
                            <Input
                                type="text"
                                name="description"
                                value={this.state.activeItem.time_start}
                                onChange={this.handleChange}
                                placeholder="Enter time start task"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">date end task</Label>
                            <Input
                                type="text"
                                name="description"
                                value={this.state.activeItem.date_end}
                                onChange={this.handleChange}
                                placeholder="Enter date end task"
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">time end task</Label>
                            <Input
                                type="text"
                                name="description"
                                value={this.state.activeItem.time_end}
                                onChange={this.handleChange}
                                placeholder="Enter time end task"
                            />
                        </FormGroup>
                        <FormGroup check>
                            <Label for="completed">
                                <Input
                                    type="checkbox"
                                    name="completed"
                                    checked={this.state.activeItem.completed}
                                    onChange={this.handleChange}
                                />
                                Completed
                            </Label>
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="success" onClick={() => onSave(this.state.activeItem)}>
                        Save
                    </Button>
                </ModalFooter>
            </Modal>
        );
    }
}
